import base64

def safe_base64_decode(s):
    s+=b"="*(4-len(s)%4)
    return base64.b64decode(s)

assert b'abcd' == safe_base64_decode(b'YWJjZA=='), safe_base64_decode('YWJjZA==')
assert b'abcd' == safe_base64_decode(b'YWJjZA'), safe_base64_decode('YWJjZA')
print('ok')

import struct
bmp_data = base64.b64decode('')
def bmp_info(data):
    #还应该判断前两位是否为BM
    turp_Pic=struct.unpack('<ccIIIIIIHH', data[:30])
    return {
        'width': turp_Pic[6],
        'height': turp_Pic[7],
        'color': turp_Pic[9]
    }

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')